import { LyModal } from '../../../../My-plugins/LyModale-plugin/LyModal.js';

export class CardsModal extends LyModal {
   constructor(...options) {
      super(...options);
      this._checkHeaderScrollbar = this._checkHeaderScrollbar();
   }
   
   close() {
      super.close();
      // document.querySelector('.header-wrapper').style.paddingRight = '20px';
      this._checkHeaderScrollbar()
   }

   open() {
      super.open();
      this._checkHeaderScrollbar()
      // document.querySelector('.header-wrapper').style.paddingRight = '37px';
   }

   _checkHeaderScrollbar() {
      let header = document.querySelector('.header-wrapper');
      let srcHeadPad = parseInt(getComputedStyle(header).paddingRight);

      return function() {
         
         header.style.paddingRight = srcHeadPad + parseInt(getComputedStyle(document.body).paddingRight) + 'px';
         console.log(srcHeadPad, parseInt(getComputedStyle(document.body).paddingRight));
      }
   }
}
