export class LyModal {
   static getConstructorParametrs() {
      return 'clientContent?, title, closable, text, setAnim, footerButtons, width';
   }

   constructor(clientContent, title, closable, text, setAnim, footerButtons, width) {
      this.options = {
         clientContent,
         title,
         closable,
         text,
         setAnim,
         footerButtons,
         width,
      }
      
      this.openCheck = true;
      this.modale = document.querySelector('.lyModal');
      this.renderModale();
      this._checkScrollbar = this._checkScrollbar();

      document.querySelectorAll('.close-lyModal').forEach((point) => {
         point.addEventListener('click', (e) => {
            this.close.call(this, e)
         });
      })
   }

   renderModale() {
      let options = this.options;

      let closableIconCheck = (options.closable) ? `<img src="https://img.icons8.com/material-outlined/34/000000/close-window.png">` : "";
      
      let modalContent = `
         <div class="lyModal__content" id="lyModal-content">
            <div class="lyModal__title-wrapper"><span class="lyModal__title">${options.title}</span></div>
            <div class="lyModal__decore-line"></div>
            <div class="lyModal__text-wrapper">
               <p class="lyModal__text">${options.text}</p>
            </div>
            <div class="lyModal__handlers">
               <div class="lyModal__close-icon close-lyModal">${closableIconCheck}</div>

               <div class="lyModal__buttons"></div>
            </div>
         </div>
      `
      
      if (options.clientContent) {
         modalContent = options.clientContent;
      }
      this.modale.innerHTML = `
         <div class="lyModal__wrapper close-lyModal animate__animated">
            ${modalContent}
         </div>
      `;
   

      options.clientContent || this._setButtonsOptions(this.options.footerButtons);

      if (!options.clientContent && options.width)
         document.querySelector('.lyModal__content').style.width = options.width;


   }

   setContent(title, text) {
      let titleModal = document.querySelector('.lyModal__title');
      let textModal = document.querySelector('.lyModal__text');
      
      titleModal.textContent = title || titleModal.textContent;
      textModal.textContent = text || textModal.textContent;
   }

   setNewButtonsOptions(options) {
      document.querySelector('.lyModal__buttons').innerHTML = '';
      
      return this._setButtonsOptions(options);
   }

   _setButtonsOptions(options) {
      if (!options) return;

      options.forEach(({ textBtn, classStylesBtn, handlerBtn }) => {
         let htmlButtonsCollection = document.querySelector('.lyModal__buttons');
         let btnHtmlContent = `<button class="${classStylesBtn}">${textBtn}</button>`;
         htmlButtonsCollection.insertAdjacentHTML('beforeend', btnHtmlContent);
         htmlButtonsCollection.lastElementChild.addEventListener('click', handlerBtn);
      })
   }
   
   open() {
      this.openCheck = false;
      document.body.style.overflow = 'hidden';
      this._checkScrollbar();
      //animate__bounce
      this.modale.classList.add('active');
      // deleteAnim(true)
      if (this.options.setAnim)
         setTimeout(this._renderAnim.bind(this, true), 500)
      // content.classList.add('.state-animate');
      //animation-name: heartBeat;

      setTimeout(() => this.openCheck = true, 500);
   }

   _close(event) {
      if (!this.openCheck) return;
      
      if (this.closeStatus || !event.target.classList.contains('close-lyModal')) return;

      this.close();
   }

   close() {
      if (this.options.setAnim)
         this._renderAnim(false)

      document.body.style.overflow = 'auto';
      this._checkScrollbar();
      
      this.modale.classList.remove('active');
   }

   _renderAnim(type) {
      if (type)
         this.modale.querySelector('.lyModal__wrapper').classList.add('animate__headShake');
      else
         this.modale.querySelector('.lyModal__wrapper').classList.remove('animate__headShake');
   }

   rubbish() {
      this.modale.innerHTML = '';
      //полностью очищает элемент lyModal
   }

   _checkScrollbar() {
      let srcWidth = document.body.clientWidth;

      return function() {
         
         if (document.body.clientWidth != srcWidth && document.body.clientWidth - srcWidth < 50)
            document.body.style.paddingRight = (document.body.clientWidth - srcWidth) + 'px';
         else
            document.body.style.paddingRight = '0px';
      }
   }


   //хуки
   onOpen() {
      this.closeStatus = false;
   }

   onClose() {
      this.closeStatus = true;
   }

};

